Ext.define('Finappsparty.controller.BeneficiaryController', {
    extend: 'Ext.app.Controller',

    config: {
        control: {
            "#beneficiaryCancelButton": {
                tap: 'onBeneficiaryCancelButtonTap'
            },
            "#beneficiaryOkButton": {
                tap: 'onBeneficiaryOkButtonTap'
            }
        }
    },

    onBeneficiaryCancelButtonTap: function(button, e, options) {
        this.cancelBeneficiaryOperation();
    },

    onBeneficiaryOkButtonTap: function(button, e, options) {
        this.acceptBeneficiaryOperation();
    },

    cancelBeneficiaryOperation: function() {
        Ext.Viewport.setMasked({
            xtype: 'loadmask',
            message: ' Operation rejected ',
            indicator: false
        });
        setTimeout(function(){
            Ext.Viewport.setMasked(false);
            Ext.getCmp('beneficiaryPanel').hide();
        },1000);

    },

    acceptBeneficiaryOperation: function() {
        var me = this;
        var bpanel = Ext.getCmp('beneficiaryPanel');
        var data = bpanel.getData();

        Ext.Viewport.setMasked({
            xtype: 'loadmask',
            message: ' Authorized ',
            indicator: false
        });

        Ext.Ajax.request({
            url: me.getApplication().getController('UrlController').getBaseUrlServices()+'accept/receive',
            params: {
                'idop': data.operationId
            },
            method: 'POST',
            success: function(response){
                Ext.Viewport.setMasked(false);
                Ext.getCmp('beneficiaryPanel').hide();
                Ext.Msg.alert('Accepted', 'Your account number is now being sent to ' + data.name);

            },
            failure: function(response){
                Ext.Viewport.setMasked(false);
                Ext.getCmp('beneficiaryPanel').hide();
                Ext.Msg.alert('Fail', 'Something has gone wrong :(');  
            }

        });


    }

});